mapper.insert:
/* 템플릿 등록, NoticeMapper.insertNotice */
SELECT CURRVAL('notice_seq') AS SEQ
INSERT INTO NOTICE ( UPPER_SEQ , LOWER_SEQ , TITLE , NOTICE , TEMPLATE , USE_YN ) VALUES ( #{upperSeq}, #{lowerSeq}, #{title}, #{notice}, #{template}, #{useYn} )

mapper.update:
/* 템플릿 수정, NoticeMapper.updateNotice */
UPDATE NOTICE SET
      UPPER_SEQ   = #{upperSeq}
    , LOWER_SEQ   = #{lowerSeq}
    , TITLE       = #{title}
    , NOTICE      = #{notice}
    , TEMPLATE    = #{template}
    , USE_YN      = #{useYn}
    , UPDATED_AT  = NOW()
WHERE SEQ         = #{seq}

/* 템플릿 수정 시 UPPER_SEQ, LOWER_SEQ와 일치하는 모든 공지사항 USE_YN 변경,  NoticeMapper.updateNoticeUseYn*/
UPDATE NOTICE SET
    USE_YN = 'N'
WHERE UPPER_SEQ    = #{upperSeq}
    AND LOWER_SEQ  = #{lowerSeq}

mapper.insert:
/* 말머리 등록 */

    INSERT INTO PREPEND (
          UPPERSEQ
        , PREPEND
    ) VALUES (
        #{seq},
        #{item}
    )

mapper.select:
select count(*) from ( select n.seq, n.title as content, case when n.use_yn = 'Y' then '사용' else '미사용' end "useYn", to_char(n.created_at, 'yyyy-mm-dd') as "createdAt", n.notice, n.template, n.upper_seq, n.use_yn, bc.name as category from notice n join base_category bc on n.lower_seq = bc.seq where 1 = 1
and n.title like concat('%', #{value}, '%')
and n.upper_seq = #{value}
and n.use_yn = #{value}
) a

select n.seq, n.title as content, case when n.use_yn = 'Y' then '사용' else '미사용' end "useYn", to_char(n.created_at, 'yyyy-mm-dd') as "createdAt", n.notice, n.template, n.upper_seq, n.use_yn, bc.name as category from notice n join base_category bc on n.lower_seq = bc.seq where 1 = 1
and n.title like concat('%', #{value}, '%')
and n.upper_seq = #{value}
and n.use_yn = #{value}
order by n.created_at desc limit #{maxPages} offset #{offset}
