mapper.select:
SELECT
    COUNT(*)
FROM tb_user
WHERE name = #{name}
    AND tel = #{tel}

mapper.insert:
insert into tb_user (
    email,
    password,
    name,
    nickname,
    tel
) values (
    #{email},
    #{password},
    #{name},
    #{nickname},
    #{tel}
);

mapper.select:
SELECT COUNT(*)
FROM tb_user
WHERE email = #{email}

SELECT email
FROM tb_user
WHERE name = #{name}
    AND tel = #{tel}

SELECT COUNT(*)
FROM tb_user
WHERE email = #{email}
    AND name = #{name}
    AND tel = #{tel}

mapper.update:
UPDATE tb_user
SET password = #{password}
WHERE email = #{email}
